import React from "react";
import { Card, CardBody, NavLink } from "reactstrap";
import { Link } from "react-router-dom";
import "./tour-card.css";
import calculateAngRating from "../utils/avgRating";

const TourCard = ({ tour }) => {
  const { id, title, photo, city, price, featured, reviews } = tour;

  const {totalRating, avgRating} = calculateAngRating(reviews)
  


  return (
    <div className="tour__card">
      <Card>
        <div className="tour__img">
          <img src={photo} alt="tour-img" />
          {featured && <span>Featured</span>}
        </div>

        <CardBody>
          <div className="card__top d-flex aling -items-center justify-content-between">
            <span className="tour__location d-flex aling-items-center gap-1">
              <i class="ri-map-pin-line"></i>
              {city}
            </span>
            <span className="tour__rating d-flex aling-items-center gap-1">
              <i class="ri-star-fill">  {avgRating === 0 ? null : avgRating}</i>
              {totalRating === 0 ?( 'Not rated' ) : ( <span>({reviews.length})</span>)}
             
              
            </span>
          </div>
          <h5 className="tour__title">
            <NavLink to={`/tours/${id}`}>{title}</NavLink>
          </h5>
          <div className="card__bottom d-flex aling-items-center justify-content-between mt-3">
            <h5>
              {" "}
              ${price} <span> /per person/</span>
            </h5>
            <button className="btn booking__btn">
              <Link to={`/tours/${id}`}>Book Now </Link>
            </button>
          </div>
        </CardBody>
      </Card>
    </div>
  );
};

export default TourCard;
